export const fetchTasks = () => {
  return async () => {
    try {
      const response = await axios.get("http://localhost:3001/tasks");
      if (response.status === 200) {
        const tasks = response.data;
        console.log("fetching");
        console.log(tasks);
        return tasks;
      } else {
        throw new Error("Failed to fetch tasks from the API.");
      }
    } catch (error) {
      console.log(error);
    }
  };
};

export const addTask = (newTask) => {
  return async (dispatch) => {
    await axios.post("http://localhost:3001/tasks", {
      title: newTask.title,
      description: newTask.description,
      deadline: newTask.deadline,
    });
    const tasks = await dispatch(fetchTasks());
    const newTaskWithId = tasks.find((task) => task.title === newTask.title && task.description === newTask.description);
    console.log(newTaskWithId);
    dispatch(taskActions.addTask(newTaskWithId));
    return newTaskWithId;
    //newTaskWithId = null;
    //return newTaskWithId;
  };
};

export const deleteTask = (id) => {
  return async () => {
    await axios.delete(`http://localhost:3001/tasks/${id}`);
  };
};

export const editTask = (editedTask) => {
  return async () => {
    const { id, title, description, deadline, completed } = editedTask;
    axios.put(`http://localhost:3001/tasks/${id}`, {
      title,
      description,
      deadline,
      completed,
    });
  };
};

