export const useInitTasks = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    const fetchTasksAndInitialize = async () => {
      try {
          dispatch(fetchTasks())
            .then((tasks) => {
              // Handle the fetched tasks
              dispatch(taskActions.setTask(tasks));
              console.log(tasks);
            })
            .catch((error) => {
              // Handle error if any
              console.log(error);
            });
      } catch (error) {
        console.log(error);
      }
    };

    fetchTasksAndInitialize();
  }, [dispatch]);
};
