// App.js

import React, { Fragment, useState } from "react";
import { useSelector } from "react-redux";
import TaskList from "./components/Tasks/TaskList";
import TaskForm from "./components/Tasks/TaskForm";
import MainHeader from "./components/Layout/MainHeader";
import useFetchTasks from "./store/taskAPI";
import "./App.css"; // Import your CSS file


function App() {
  const showTaskForm = useSelector((state) => state.ui.showTaskFormModal);
  const [isLoaded, setIsLoaded] = useState(false);

  const { setTasks } = useFetchTasks(); // Call the useFetchTasks hook and destructure the setTasks function

  const handleUpdateTasks = (tasks) => {
    setTasks(tasks); // Call the setTasks function to update tasks in the Redux store
  };

  return (
    <Fragment>
      <MainHeader />
      {showTaskForm && <TaskForm onUpdateTasks={handleUpdateTasks} />}
      <main>
        {!isLoaded ? (
          <div className="loading-container">
            <p className="loading-text">Loading</p>
            <div className="loading-dots">
              <div className="dot"></div>
              <div className="dot"></div>
              <div className="dot"></div>
            </div>
          </div>
        ) : (
          <TaskList />
        )}
      </main>
    </Fragment>
  );
}

export default App;
