import { configureStore } from '@reduxjs/toolkit';
import taskReducer, { fetchTasks } from './task-slice';
import uiReducer from './ui-slice';

const store = configureStore({
  reducer: {
    task: taskReducer,
    ui: uiReducer,
  },
});

store.dispatch(fetchTasks());

export default store;
