import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

/* 
    tasks: array of task objects -> { title:___, desc:___, deadline:___ }, 'title' & 'desc' are Strings, 'deadline' is a Date object
*/
const initialTaskState = {
  tasks: [],
  //quantity: 0,
};

const taskSlice = createSlice({
  name: "task",
  initialState: initialTaskState,
  reducers: {
    addTask(state, action) {
      // action will pass an entire task object
      const task = action.payload;
      console.log("From task-slice addTask");
      console.log(task);

      axios
        .post("http://localhost:3001/tasks", {
          title: task.title,
          description: task.desc,
          deadline: task.deadline,
        })
        .then((response) => {
          // Handle the successful response
          const createdTask = response.data;
          // Add the created task to the state
          state.tasks.push(createdTask);
        })
        .catch((error) => {
          // Handle any errors
          console.log(error.message);
        });
      state.tasks.sort((a, b) => new Date(a.deadline) - new Date(b.deadline));
    },
    removeTask(state, action) {
      // action will pass the id of a task
      const id = action.payload;

      // double confirm deletion
      const proceed = window.confirm("Delete task?");

      if (proceed) {
        state.tasks = state.tasks.filter((task) => task._id !== id);
      }
    },
    editTask(state, action) {
      // action will pass the edited task
      const { id, title, desc, deadline } = action.payload;

      // Make the PUT request to update the task on the backend
      axios
        .put(`http://localhost:3001/tasks/${id}`, { title, desc, deadline })
        .then((response) => {
          // Handle the successful response
          const updatedTask = response.data;

          // Update the task in the state with the updated values
          const updatedTasks = state.tasks.map((task) => {
            if (task.id === id) {
              return updatedTask;
            }
            return task;
          });

          state.tasks = updatedTasks;
        })
        .catch((error) => {
          // Handle any errors
          console.log(error.message);
        });
    },
    setTask(state, action) {
		console.log(action.payload);
      state.tasks = action.payload;
    },
  },
});

export const taskActions = taskSlice.actions;
export default taskSlice;
