import React, { Fragment, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import TaskList from './components/Tasks/TaskList';
import TaskForm from './components/Tasks/TaskForm';
import MainHeader from './components/Layout/MainHeader';
import { uiActions } from './store/ui-slice';
import { taskActions } from './store/task-slice';

function App() {
  const showTaskForm = useSelector((state) => state.ui.showTaskFormModal);
  const dispatch = useDispatch();
  const tasks = useSelector((state) => state.task.tasks);

  useEffect(() => {
    // Fetch tasks from the API when the component mounts
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://localhost:3001/tasks'); // Replace with the appropriate API endpoint
        if (!response.ok) {
          throw new Error('Failed to fetch tasks from the API.');
        }
        const data = await response.json();
		tasks = data;
        dispatch(taskActions.setTasks(data)); // Dispatch an action to store the fetched tasks in the Redux store
      } catch (error) {
        console.log(error);
      }
    };

    fetchTasks();
  }, [dispatch]);

  const showAddTaskFormHandler = () => {
    dispatch(taskActions.hideTaskForm()); // Reset the task form state
    dispatch(uiActions.showAddTaskForm()); // Show the add task form
  };

  return (
    <Fragment>
      <MainHeader />
      {showTaskForm && <TaskForm />}
      <main>
        <TaskList tasks={tasks} /> {/* Pass the tasks to the TaskList component */}
        <button onClick={showAddTaskFormHandler}>Add Task</button>
      </main>
    </Fragment>
  );
}

export default App;
