import React, { Fragment, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import TaskList from "./components/Tasks/TaskList";
import TaskForm from "./components/Tasks/TaskForm";
import MainHeader from "./components/Layout/MainHeader";
import { fetchTasks } from "./store/useFetchTasks";
import taskActions from "./store/task-slice";

function App() {
  const showTaskForm = useSelector((state) => state.ui.showTaskFormModal);
  const isLoaded = useSelector((state) => state.ui.isLoaded);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchTasks());
  }, [dispatch]);

  useEffect(() => {
    // Set isLoaded to true once tasks are fetched
    dispatch(taskActions.setTask([]));
  }, [dispatch]);

  return (
    <Fragment>
      <MainHeader />
      {showTaskForm && <TaskForm />}
      <main>
        {!isLoaded ? (
          <div className="loading-container">
            <p className="loading-text">Loading</p>
            <div className="loading-dots">
              <div className="dot"></div>
              <div className="dot"></div>
              <div className="dot"></div>
            </div>
          </div>
        ) : (
          <TaskList />
        )}
      </main>
    </Fragment>
  );
}

export default App;
