import { useState, Fragment } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import TaskItem from "./TaskItem";
import classes from "./TaskList.module.css";
import { taskActions } from "../../store/task-slice";
const API_ENDPOINT = "http://localhost:3000";



function TaskList() {
	const [filterTitle, setFilterTitle] = useState("");

//   const tasks = useSelector((state) => state.task.tasks);
	const tasks = useSelector((state) => state.task.tasks);
	useEffect(() => {
		fetch(`${API_ENDPOINT}/tasks`)
			.then((response) => response.json())
			.then((data) => {
				// Update the tasks in the Redux store
				dispatch(taskActions.setTasks(data));
			})
			.catch((error) => {
				console.log(error);
			});
	}, []);


  const filterHandler = (event) => {
	setFilterTitle(event.target.value);
  };

  const filteredTasks = tasks.filter((task) => task.title.includes(filterTitle));

  return (
    <Fragment>
      <input className={classes["task-finder"]} onChange={filterHandler}/>
      <div className={classes["task-list"]}>
        {filteredTasks.map((task) => (
          <TaskItem
            key={task.id}
            id={task.id}
            title={task.title}
            desc={task.desc}
            deadline={task.deadline}
          />
        ))}
      </div>
    </Fragment>
  );
}

export default TaskList;
