import { useSelector, useDispatch } from "react-redux";
import { taskActions } from "../../store/task-slice";
import { useRef } from "react";
import Modal from "../UI/Modal";
import classes from "./TaskForm.module.css";
import { uiActions } from "../../store/ui-slice";
import { addTask, editTask, fetchTasks } from "../../store/useFetchTasks";

function TaskForm() {
  const dispatch = useDispatch();

  // const isEdit = useSelector((state) => state.ui.isEdit)
  const task = useSelector((state) => state.ui.task);

  // I forgor whats the best way to perform input validation :/
  const titleRef = useRef();
  const descRef = useRef();
  const deadlineRef = useRef();

  const isEditTask = task ? true : false;

  console.log("isEditTask: " + isEditTask);

  function onHide() {
    dispatch(uiActions.hideTaskForm());
  }

  function submitHandler(event) {
    event.preventDefault();

    // get the submitted values
    const enteredTitle = titleRef.current.value;
    const enteredDesc = descRef.current.value;
    const enteredDeadline = deadlineRef.current.value;

    // perform checks
    if (
      enteredTitle.trim().length === 0 ||
      enteredDesc.trim().length === 0 ||
      enteredDeadline.trim().length === 0
    ) {
      // report error (somehow), prolly by changing the styles
      return;
    }

    const editedTask = {
      ...task,
      title: enteredTitle,
      description: enteredDesc,
      deadline: enteredDeadline,
    };

    if (isEditTask) {
      console.log("in taskform, edited task:", editedTask);
		dispatch(editTask(editedTask));
        dispatch(taskActions.editTask(editedTask))


      //dispatch(fetchTasks());

    } else {
    //backend
	const newTaskWithId = dispatch(addTask(editedTask));
	//frontend
	//return task with correct id
	//dispatch(taskActions.addTask(newTaskWithId));

    }

    onHide();
  }

  return (
    <Modal onHide={onHide}>
      <form onSubmit={submitHandler}>
        <div className={classes.form}>
          <p>
            <label htmlFor="title">Title</label>
            <input
              ref={titleRef}
              id="title"
              type="text"
              name="title"
              placeholder="Insert title here..."
              defaultValue={task ? task.title : ""}
              required
            />
          </p>
          <p>
            <label htmlFor="desc">Description</label>
            <textarea
              ref={descRef}
              id="desc"
              type="text"
              rows="5"
              name="desc"
              placeholder="Insert description here..."
              defaultValue={task ? task.desc : ""}
              required
            />
          </p>
          <p>
            <label htmlFor="deadline">Deadline</label>
            <input
              ref={deadlineRef}
              id="deadline"
              type="date"
              name="deadline"
              min="2023-05-01"
              max="2023-12-15"
              defaultValue={task ? task.deadline : ""}
              required
            />
          </p>
        </div>
        <div className={classes.actions}>
          <button type="button" onClick={onHide}>
            Cancel
          </button>
          <button>{task ? "Update task" : "Add task"}</button>
        </div>
      </form>
    </Modal>
  );
}

export default TaskForm;
